viewmodel_component.correct_example:
import Foundation
import Combine

final class LoginViewModel: ObservableObject {
  // MARK: - Published State (Business State ONLY)
  @Published private(set) var email: String = ""
  @Published private(set) var password: String = ""
  @Published private(set) var status: Status = .idle
  @Published private(set) var errorMessage: String?

  enum Status: Equatable {
    case idle
    case loading
    case success(User)
    case error
  }

  // MARK: - Dependencies
  private let loginUseCase: LoginUserUseCaseProtocol
  private let analyticsService: AnalyticsServiceProtocol
  private var cancellables = Set<AnyCancellable>()

  // MARK: - Computed Properties
  var isLoading: Bool {
    if case .loading = status { return true }
    return false
  }

  var isFormValid: Bool {
    !email.isEmpty && email.contains("@") && !password.isEmpty
  }

  // MARK: - Init
  init(
    loginUseCase: LoginUserUseCaseProtocol,
    analyticsService: AnalyticsServiceProtocol
  ) {
    self.loginUseCase = loginUseCase
    self.analyticsService = analyticsService
  }

  // MARK: - User Actions (Methods replace Action enum)
  func emailChanged(_ newEmail: String) {
    email = newEmail
  }

  func passwordChanged(_ newPassword: String) {
    password = newPassword
  }

  func loginButtonTapped() {
    guard isFormValid else {
      errorMessage = "Invalid form"
      return
    }

    status = .loading
    errorMessage = nil

    Task {
      do {
        let user = try await loginUseCase.execute(
          email: email,
          password: password
        )
        await MainActor.run {
          status = .success(user)
        }
        await analyticsService.track(event: .login)
      } catch {
        await MainActor.run {
          status = .error
          errorMessage = error.localizedDescription
        }
      }
    }
  }
}

