class LoginViewModel: ObservableObject {
  var onLoginSuccess: ((User) -> Void)?
  var onCancel: (() -> Void)?

  func loginButtonTapped() async {
    // ... login logic
    if case .success(let user) = status {
      onLoginSuccess?(user)
    }
  }
}

