state_pattern.example:
@Published var destination: Destination?

enum Destination: Identifiable {
  case taskDetail(SmartTask)
  case addTask

  var id: String {
    switch self {
    case .taskDetail(let task): return "detail-\(task.id)"
    case .addTask: return "add"
    }
  }
}

// In View
.sheet(item: $viewModel.destination) { destination in
  switch destination {
  case .taskDetail(let task):
    TaskDetailView(task: task)
  case .addTask:
    AddTaskView()
  }
}

coordinator_pattern.example:
class AuthCoordinator {
  func showLogin() {
    let viewModel = LoginViewModel(
      loginUseCase: Resolver.resolve(),
      analyticsService: Resolver.resolve()
    )
    viewModel.onLoginSuccess = { [weak self] user in
      self?.showHome(user: user)
    }

    let view = LoginView(viewModel: viewModel)
    let vc = UIHostingController(rootView: view)
    navigationController.push(vc, animated: true)
  }
}

